import {useState,useRef,useEffect} from 'react';
import { Link } from "react-router-dom";
import Form from "../../utilities/Forms";
import ReCAPTCHA from 'react-google-recaptcha';
import axios from 'axios';
// import REACT_APP_SITE_KEY from "././site_key";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [remember, setRemember] = useState(false);
  const [validate, setValidate] = useState({});
  const [showPassword, setShowPassword] = useState(false);

  const captchaRef = useRef(null);

useEffect(()=>{
  testingGet()
},[])

const testingGet =() => {
  return fetch("https://se-event-management.azurewebsites.net/User").then((data)=>console.log(data,"api response"))
}
  const validateLogin = () => {
    let isValid = true;

    let validator = Form.validator({
      email: {
        value: email,
        isRequired: true,
        isEmail: true,
      },
      password: {
        value: password,
        isRequired: true,
        minLength: 6,
      },
    });

    if (validator !== null) {
      setValidate({
        validate: validator.errors,
      });

      isValid = false;
    }
    return isValid;
  };

  const authenticate = (e) => {
    e.preventDefault();

    const validate = validateLogin();

    if (validate) {
      setValidate({});
      setEmail("");
      setPassword("");
      alert("Successfully Login");
    }
  };

const onRecaptchaChange= (value)=> {
  console.log(value,"value");
}
  // NEWLY ADDED
//   const handleSubmit = async e => {
//     e.preventDefault();
//     setError('');
//     setMessage('');
//     if(email && password){
//     let token = captchaRef.current.getValue();
//     if(token){
//         let valid_token = await verifyToken(token);
//         if(valid_token.success){
//         setMessage("Hurray!! you have submitted the form");
//         }else{
//         setError("Sorry!! Token invalid");
//         }
//     }else{
//         setError("You must confirm you are not a robot");
//     }
//     }else{
//     setError("email and password are required");
//     }
//  };

//  const verifyToken = async (token) => {
//   try{
//   let response = await axios.post(`http://localhost:4000/verify-token`,{

// secret:process.env.REACT_APP_SECRET_KEY,
//       token
//   },console.log(token));
//   return response.data;
//   }catch(error){
//   console.log("error ",error);
//   }
// };

//  NEWLY ADDED

  const togglePassword = (e) => {
    if (showPassword) {
      setShowPassword(false);
    } else {
      setShowPassword(true);
    }
  };

  return (
    <div className="row g-0 auth-wrapper">
      {/* <div className="col-12 col-md-5 col-lg-6 h-100 auth-background-col">
        <div className="auth-background-holder"></div>
        <div className="auth-background-mask"></div>
      </div> */}

      <div className="col-12 col-md-12 col-lg-12 auth-main-col text-center">
        <div className="d-flex flex-column align-content-end">
          <div className="auth-body mx-auto">
            <p>Login to your account</p>
            <div className="auth-form-container text-start">
              <form
                className="auth-form"
                method="POST"
                onSubmit={authenticate}
                autoComplete={"off"}
              >
                <div className="email mb-3">
                  <input
                    type="email"
                    className={`form-control ${
                      validate.validate && validate.validate.email
                        ? "is-invalid "
                        : ""
                    }`}
                    id="email"
                    name="email"
                    value={email}
                    placeholder="Email"
                    onChange={(e) => setEmail(e.target.value)}
                  />

                  <div
                    className={`invalid-feedback text-start ${
                      validate.validate && validate.validate.email
                        ? "d-block"
                        : "d-none"
                    }`}
                  >
                    {validate.validate && validate.validate.email
                      ? validate.validate.email[0]
                      : ""}
                  </div>
                </div>

                <div className="password mb-3">
                  <div className="input-group">
                    <input
                      type={showPassword ? "text" : "password"}
                      className={`form-control ${
                        validate.validate && validate.validate.password
                          ? "is-invalid "
                          : ""
                      }`}
                      name="password"
                      id="password"
                      value={password}
                      placeholder="Password"
                      onChange={(e) => setPassword(e.target.value)}
                    />

                    <button
                      type="button"
                      className="btn btn-outline-primary btn-sm"
                      onClick={(e) => togglePassword(e)}
                    >
                      <i
                        className={
                          showPassword ? "far fa-eye" : "far fa-eye-slash"
                        }
                      ></i>{" "}
                    </button>

                    <div
                      className={`invalid-feedback text-start ${
                        validate.validate && validate.validate.password
                          ? "d-block"
                          : "d-none"
                      }`}
                    >
                      {validate.validate && validate.validate.password
                        ? validate.validate.password[0]
                        : ""}
                    </div>
                  </div>

                  {/* NEWLY ADDED */}
                  <div className='formGroup'>
                      <ReCAPTCHA sitekey="6LffFXEkAAAAAAuTtmg_OKXkC7MI07-50OyeoBdb" ref={captchaRef} onChange={onRecaptchaChange}  />
                  </div>

                {/* NEWLY ADDED */}

                  <div className="extra mt-3 row justify-content-between">
                    <div className="col-6">
                      <div className="form-check">
                        <input
                          className="form-check-input"
                          type="checkbox"
                          id="remember"
                          checked={remember}
                          onChange={(e) => setRemember(e.currentTarget.checked)}
                        />
                        <label className="form-check-label" htmlFor="remember">
                          Remember me
                        </label>
                      </div>
                    </div>
                    <div className="col-6">
                      <div className="forgot-password text-end">
                        <Link to="/forgot-password">Forgot password?</Link>
                      </div>
                    </div>
                  </div>
                </div>
                <div className="text-center">
                  <button
                    type="submit"
                    className="btn btn-primary w-100 theme-btn mx-auto">
                    Log In
                  </button>
                </div>
              </form>

              <hr />
              <div className="auth-option text-center pt-2">
                No Account?{" "}
                <Link className="text-link" to="/register">
                  Sign up{" "}
                </Link>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
