import React from "react";
import { useState, useRef, useEffect } from "react";
import axios from "axios";
import EventsNavbar from "./EventsNavbar";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";

function Events() {
  const [Events, setEvents] = useState([]);

  useEffect(() => {
    axios
      .get("https://se-event-management.azurewebsites.net/Event")
      // .then((data) => data.json())
      // .then((response) => console.log(response, "api response"));
      .then((response) => {
        setEvents(response?.data);
        console.log(response);
      });
  }, []);
  return (
    <>
      <EventsNavbar />

      <div
        style={{
          display: "flex",
          height: "300px",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <h2>ALL EVENTS</h2>
      </div>
      {/* <div>
        {Events.length &&
          Events.map((event) => {
            return <div>Event Name: {event.eventName}</div>;
          })}
      </div> */}
      <div>
        {Events.length &&
          Events.map((event) => {
            return (
              <div>
                <Row xs={1} md={2} className="g-4">
                  {Array.from({ length:Events.length  }).map((_, idx) => (
                    <Col>
                      <Card style={{ width: "18rem" }}>
                        <Card.Img variant="top" src="event1.jpeg" />
                        <Card.Body>
                          <Card.Title>{event.eventName}</Card.Title>
                          <Card.Text>
                            Some quick example text to build on the card title
                            and make up the bulk of the card's content.
                          </Card.Text>
                          <Card.Text> City:{event.eventCity} </Card.Text>
                          <Card.Text>State: {event.eventState}</Card.Text>
                          <Card.Text>Address:{event.eventAddress}</Card.Text>
                          <Button variant="primary">BOOK</Button>
                        </Card.Body>
                      </Card>
                    </Col>
                  ))}
                </Row>
              </div>
            );
          })}
      </div>
    </>
  );
}

export default Events;
