import React from "react";
import { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import FileUpload from "../../utilities/FileUpload";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
// import {EventsNavbar} from "./views/EventsNavbar";

function UploadProduct() {
  const [TitleValue, setTitleValue] = useState("");
  const [SportValue, setSportValue] = useState("");
  const [DescriptionValue, setDescriptionValue] = useState("");
  const [CityValue, setCityValue] = useState("");
  const [StateValue, setStateValue] = useState("");
  const [AddressValue, setAddressValue] = useState("");
  // const [startDate, setStartDate] = useState(new Date());
  const [startDate, setStartDate] = useState(new Date());
  const [endDate, setEndDate] = useState(new Date());

  const onTitleChange = (event) => {
    setTitleValue(event.currentTarget.value);
  };
  const onSportChange = (event) => {
    setSportValue(event.currentTarget.value);
  };
  const onDescriptionChange = (event) => {
    setDescriptionValue(event.currentTarget.value);
  };

  const onCityChange = (event) => {
    setCityValue(event.currentTarget.value);
  };

  const onStateChange = (event) => {
    setStateValue(event.currentTarget.value);
  };

  const onAddressChange = (event) => {
    setAddressValue(event.currentTarget.value);
  };

  // const updateImages = (newImages) => {
  //     setImages(newImages)
  // }
  const onSubmit = (event) => {
    event.preventDefault();
  };

  return (
    <div>
      {/* <EventsNavbar/> */}
    <div style={{ maxWidth: "700px", margin: "2rem auto" }}>
      <div style={{ textAlign: "center", marginBottom: "2rem" }}>
        <h2 level={2} className="text-center mt-5 mb-5">
          Upload Event
        </h2>
      </div>

      <form onSubmit={onSubmit} className="mt-5 mb-5">
        {/* DropZone */}

        <label>Event Name</label>
        <input
          className="form-control"
          onChange={onTitleChange}
          value={TitleValue}
        />
        <br />
        <label>Sport Name</label>
        <input
          className="form-control"
          onChange={onSportChange}
          value={SportValue}
        />
        <br/>
        <label>Event Description</label>
        <textarea
          class="form-control"
          rows="5"
          onChange={onDescriptionChange}
          value={DescriptionValue}
        />
        <br></br>
        <label>Event Start Time</label>
        <DatePicker
          selected={startDate}
          onChange={(date) => setStartDate(date)}
          timeInputLabel="Time:"
          dateFormat="MM/dd/yyyy h:mm:ss aa"
          showTimeInput
        />
        <br></br>
        <br></br>
        <label>Event End Time</label>
        <DatePicker
          selected={endDate}
          onChange={(date) => setEndDate(date)}
          timeInputLabel="Time:"
          dateFormat="MM/dd/yyyy h:mm:ss aa"
          showTimeInput
        />
        <br></br>
        <br/>
        <label>City</label>
        <input
          className="form-control"
          onChange={onCityChange}
          value={CityValue}
        />
        <br />
        <label>State</label>
        <input
          className="form-control"
          onChange={onStateChange}
          value={StateValue}
        />
        <br />

        <label>Address</label>
        <input
          className="form-control"
          onChange={onAddressChange}
          value={AddressValue}
        />
        <br />
        {/* <label>Upload Image</label>
        <FileUpload />
        <br /> */}
        <button className="btn btn-primary" onClick={onSubmit}>
          SUBMIT
        </button>
      </form>
    </div>
    </div>
  );
}

export default UploadProduct;
